  `no` INTEGER PRIMARY KEY AUTOINCREMENT,
  `year` INTEGER,
  `times` INTEGER,
  `subject` INTEGER,
  `req` INTEGER,
  `obj_item` INTEGER,
  `m_key` INTEGER,
  `content` varchar(512),
