INSERT INTO `minbeop` (`no`, `year`, `times`, `subject`, `req`, `obj_item`, `m_key`, `content`, `correct`, `level`, `OX`, `note_keyword`) VALUES
(1, 17, 28, 20, 41, 0, 172820410, '법률행위 등에 관한 설명으로 틀린 것은? (다툼이 잇으면 판례에 따름)', '1', '', '', ''),
(2, 17, 28, 20, 41, 1, 172820411, '기성조건을 정지조건으로 한 법률행위는 무효이다.', '', '', 'X', ''),
(3, 17, 28, 20, 41, 2, 172820412, '의사표시가 발송된 후라도 도달하기 전에 표의자는 그 의사표시를 철회할 수 있다.', '', '', 'O', ''),
(4, 17, 28, 20, 41, 3, 172820413, '어떤 해악의 고지없이 단순히 각서에 서명날인 할 것만을 강력히 요구한 행위는 강박에 의한 의사표시의 강박행위가 아니다.', '', '', 'O', ''),
(5, 17, 28, 20, 41, 4, 172820414, '표의자가 과실없이 상대방의 소재를 알지 못한 경우에는 민사소송법의 공시송달규정에 의하여 의사표시를 송달할 수 있다.', '', '', 'O', ''),
(6, 17, 28, 20, 41, 5, 172820415, '농지자격취득증명은 농지취득의 원인이 되는 매매계약의 효력발생요건이 아니다.', '', '', 'O', ''),
(7, 17, 28, 20, 42, 0, 172820420, '뮤효와 취소에 관한 설명으로 틀린 것은? (다툼이 있으면 판례에 따름)', '1', '', '', 'ㅇ'),
(8, 17, 28, 20, 42, 1, 172820421, '무효인 가등기를 유효한 등기로 전용하기로 약정하면 그 가등기는 소급하여 유효한 등기가 된다.', '', '', 'X', ''),
(9, 17, 28, 20, 42, 2, 172820422, '취소권은 추인할 수 있는 날로부터 3년 내에, 법률행위를 한 날로부터 10년내에 행사하여야 한다.', '', '', 'O', ''),
(10, 17, 28, 20, 42, 3, 172820423, '뮤효인 법률행위를 사후에 적법하게 추인한 때에는 다른 정함이 없으면 새로운 법률행위를 한 것으로 보아야 한다.', '', '', 'O', ''),
(11, 17, 28, 20, 42, 4, 172820424, '무권리자가 甲의 권리를 자기의 이름으로 처분한 경우, 甲이 그 처분을 추인하면 처분행위의 효력이 甲에게 미친다.', '', '', 'O', ''),
(12, 17, 28, 20, 42, 5, 172820425, '무효행위의 추인은 그 무효원인이 소멸한 후에 하여야 그 효력이 있다.', '', '', 'O', ''),
(13, 17, 28, 20, 43, 0, 172820430, '대리에 관한 설명으로 틀린 것은? (다툼이 있으면 판례에 따름)', '3', '', '', ''),
(14, 17, 28, 20, 43, 1, 172820431, '대리행위가 강행법규에 위반하여 무효가 된 경우에 표현대리가 적용되지 아니한다.', '', '', 'O', ''),
(15, 17, 28, 20, 43, 2, 172820432, '본인의 허락이 없는 자기계약이라도 본인이 추인하면 유효한 대리행위로 될 수 있다.', '', '', 'O', ''),
(16, 17, 28, 20, 43, 3, 172820433, '상대방 없는 단독행위와 무권대리는 본인의 추인 여부와 관계없이 확정적으로 유효하다.', '', '', 'X', ''),
(17, 17, 28, 20, 43, 4, 172820434, '대리인이 자기의 이익을 위한 배임적 의사표시를 하였고 상대방도 이를 안 경우, 본인은 그 대리인의 행위에 대하여 책임이 없다.', '', '', 'O', ''),
(18, 17, 28, 20, 43, 5, 172820435, '권한을 정하지 아니한 임의대리인은 본인의 미등기부동산에 관한 보존등기를 할 수 있다.', '', '', 'O', ''),
(19, 17, 28, 20, 44, 0, 172820440, '대리권이 없는 乙이 甲을 대리하여 丙에게 甲 소유의 토지를 매도하였다. 다음 설명 중 틀린 것은? (다툼이 있으면 판례에 따름)', '2', '', '', ''),
(20, 17, 28, 20, 44, 1, 172820441, '乙이 甲을 단독상속한 경우, 乙은 본인의 지위에서 추인거절권을 행사할 수 있다.', '', '', '', ''),
(21, 17, 28, 20, 44, 2, 172820442, '乙과 계약을 체결한 丙은 甲의 추인의 상대방이 될 수 없다.', '', '', '', ''),
(22, 17, 28, 20, 44, 3, 172820443, '甲의 추인은 그 무권대리행위가 있음을 알고 이를 추인하여야 그 행위의 효과가 甲에게 귀속된다.', '', '', '', ''),
(23, 17, 28, 20, 44, 4, 172820444, '甲이 을에게 추인한 경우에 병이 추인이 있어던 사실을 알지 못한 때에는 甲은 병에게 추인의 효과를 주장하지 못한다.', '', '', '', ''),
(24, 17, 28, 20, 44, 5, 172820445, '만약 을이 미성년자라면, 甲이 을의 대리행위에 대해 추인을 거절하더라도 병은 을에 대해 계약의 이행이나 손해배상을 청구할 수 없다.', '', '', '', ''),
(25, 17, 28, 20, 45, 0, 172820450, '다음 중 무효가 아닌 것은? (다툼이 있으면 판례에 따름)', '2', '', '', ''),
(26, 17, 28, 20, 45, 1, 172820451, '상대방과 통정하여 허위로 체결한 매매계약', '', '', '', ''),
(27, 17, 28, 20, 45, 2, 172820452, '주택법의 전매행위제한을 위반하여 한 전매 약정', '', '', '', ''),
(28, 17, 28, 20, 45, 3, 172820453, '관할관청의 허가 없이 한 학교법인의 기본재산 처분', '', '', '', ''),
(29, 17, 28, 20, 45, 4, 172820454, '도박채무를 변제하기 위하여 그 채권자와 체결한 토지양도계약', '', '', '', ''),
(30, 17, 28, 20, 45, 5, 172820455, '공무원의 직무에 관하여 청탁하고 그 대가로 돈을 지급할 것을 내용으로 한 약정', '', '', '', ''),
(31, 17, 28, 20, 46, 0, 172820460, '조건부 법률행위에 관한 설명으로 틀린 것은? (다툼이 있으면 판례에 따름)', '3', '', '', ''),
(32, 17, 28, 20, 46, 1, 172820461, '상대방이 동의하면 채무면제에 조건을 붙일 수 있다.', '', '', '', ''),
(33, 17, 28, 20, 46, 2, 172820462, '정지조건부 법률행위는 조건이 불성취로 확정되면 무효로 된다.', '', '', '', ''),
(34, 17, 28, 20, 46, 3, 172820463, '조건을 붙이는 것이 허용되지 않는 법률행위에 조건을 붙인 경우, 다른 정함이 없으면 그 조건만 분리하여 무효로 할 수 있다.', '', '', '', ''),
(35, 17, 28, 20, 46, 4, 172820464, '당사자가 조건성취의 효력을 그 성취전에 소급하게 할 의사를 표시한 때에는 그 의사에 의한다.', '', '', '', ''),
(36, 17, 28, 20, 46, 5, 172820465, '정지조건의 경우에는 권리를 취득한 자가 조건성취에 대한 증명책임을 부담한다,', '', '', '', ''),
(37, 17, 28, 20, 47, 0, 172820470, '불공정한 법률행위(민법 제104조)에 관한 설명으로 틀린 것은? (다툼이 있으면 판례에 따름)', '4', '', '', ''),
(38, 17, 28, 20, 47, 1, 172820471, '경매에는 적용되지 않는다.', '', '', '', ''),
(39, 17, 28, 20, 47, 2, 172820472, '무상계약에는 적용되지 않는다.', '', '', '', ''),
(40, 17, 28, 20, 47, 3, 172820473, '불공정한 법률행위에 무효행위 전환의 법리가 적용될 수 있다.', '', '', '', ''),
(41, 17, 28, 20, 47, 4, 172820474, '법률행위가 대리인에 의하여 행해진 경우, 궁박 상태는 대리인을 기준으로 판단하여야 한다.', '', '', '', ''),
(42, 17, 28, 20, 47, 5, 172820475, '매매계약이 불공정한 법률행위에 해당하는지는 계약체결 당시를 기준으로 판단하여야 한다.', '', '', '', ''),
(43, 17, 28, 20, 48, 0, 172820480, '다음 중 서로 잘못 짝지어진 것은?', '1', '', '', ''),
